import React, {useRef} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup'

const TodoForm = (props) => {
	const title = useRef(),
		text = useRef(),
		endDate = useRef()

	function setData(e){
		e.preventDefault()
		console.log(Date.now())
		const currentDate = Date.now(),
			newTask = {
			id: currentDate,
			title: title.current.value,
			text: text.current.value,
			startDate: `${currentDate.getDate}.${currentDate.getMonth()}`,
			endDate: endDate.current.value
		}
		console.log(newTask)

		title.current.value = ""
		text.current.value = ""
	}

	return (
		<div>
			<InputGroup className="p-3 pb-1">
				<Form.Control placeholder='title' ref={title}/>
				<Form.Control placeholder='text' ref={text}/>
				<Form.Control placeholder='deadline' ref={endDate}/>
				<Button variant="outline-success" onClick={e=>setData(e)}>create</Button>
			</InputGroup>
		</div>
	);
};

export default TodoForm;
