import React, {useState} from 'react'
import TodoItem from "./todoItem"
import TodoForm from "./todoForm";
import {TrashIcon} from "@primer/octicons-react";
import Button from "react-bootstrap/Button";

const TodoGroup = ({data, deleteGroup}) => {
	// console.log(data.title)

	const [groupData, dataHandler] = useState([] )

	const addTodo = (newTodo) => {
		dataHandler([...groupData, newTodo])
	}
	const deleteItem = (deleteItemId) => {
		dataHandler(groupData.filter( el => el.id !== deleteItemId))
	}

	return (
		<div className="bg-opacity-25 bg-black m-3 mt-4 p-4 rounded-3">
			<h1 className="fs-2 fw-bold text-center text-primary">
				{data.title}
				<div className="tools">
					<Button onClick={()=>deleteGroup(data.id)} variant="" className="p-1 d-flex align-content-center">
						<TrashIcon size={24}/>
					</Button>
				</div>
			</h1>
			<TodoForm addTodo={addTodo}/>
			{
				groupData.map( (todoElData, i) =>
					<TodoItem cnt={i+1} data={todoElData} key={todoElData.id} deleteItem={deleteItem}/>
				)
			}
		</div>
	)
}

export default TodoGroup
