import React, {useState} from 'react'
import TodoItem from "./todoItem"
import TodoForm from "./todoForm";

const TodoGroup = ({data}) => {

	const [groupData, dataHandler] = useState([
		{
			id: 1,
			title: "Card.Header",
			text: "make different state of task",
			startDate: "2022-10-22",
			endDate: "2022-11-16",
			// state: 'passive', 'in progress', 'done', 'deadlines close', 'too late'
		},
	] )

	const addTodo = (newTodo) => {
		dataHandler([...groupData, newTodo])
	}
	const deleteItem = (deleteItemId) => {
		dataHandler(groupData.filter( el => el.id !== deleteItemId))
	}

	return (
		<div className="todo">
			<h1 className="fs-2 fw-bold text-center"> {data.title} </h1>
			<TodoForm addTodo={addTodo}/>
			{
				groupData.map( (todoElData, i) =>
					<TodoItem cnt={i+1} data={todoElData} key={todoElData.id} deleteItem={deleteItem}/>
				)
			}
		</div>
	)
}

export default TodoGroup
