import React, {useRef} from 'react'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import InputGroup from 'react-bootstrap/InputGroup'

const NewGroupForm = ({newGroup}) => {

	const title = useRef()

	const setData = () => {
		const newGroupData = {
			id: Date.now(),
			title: title
		}
		newGroup(newGroupData)
	}

	return (
		<div>
			<InputGroup className="p-3 pb-1">
				<Form.Control placeholder='New group title' ref={title}/>
				<Button variant="outline-success" onClick={e=>setData(e)}>create new group</Button>
			</InputGroup>
		</div>
	);
};

export default NewGroupForm;
