import React, {useState} from 'react'
import './styles/reset.css'
import TodoGroup from "./.components/todoGroup";
import NewGroupForm from "./.components/newGroupForm";


function App() {
    const [groupsData, groupsDataHandler] = useState( [{id:0, title:"first group"}] )

    const newGroup = (newGroupData) => {
        groupsDataHandler([...groupsData, newGroupData])
    }
    const deleteGroup = (deleteGroupId) => {
        groupsDataHandler(groupsData.filter( el => el.id !== deleteGroupId))
    }

    return (
        <div className="App">

            <NewGroupForm newGroup={newGroup}/>
            {
                groupsData.map( groupData =>
                    // console.log(groupData)
                    <TodoGroup data={groupData} deleteGroup={deleteGroup}/>
                )
            }

        </div>
    )
}

export default App
