import React from 'react'
import TodoItem from "./todoItem"
import TodoForm from "./todoForm";

const TodoGroup = ({props, create}) => {
	const createEl = (newPost) => {
		create(newPost)
	}
	return (
		<div className="todo">
			<h1 style={{textAlign: 'center'}}> {props.data.title} </h1>
			<TodoForm addTodo={createEl}/>
			{
				props.data.map( todoElData =>
					<TodoItem data={todoElData} key={todoElData.id}/>
				)
			}
		</div>
	)
}

export default TodoGroup
