import React, {useState} from 'react'

function App() {
  const [cnt, cntHandler]  = useState(0)
  const [inputValue, inputValueChange]  = useState("type smth")

  function cntp () {
    cntHandler( cnt + 1 )
  }
  function cntm () {
    cntHandler( cnt - 1 )
  }
  function inputValueHandler( arg ){
    inputValueChange(arg?arg:"-")
  }

  return (
    <div className="App">
      <h1>{cnt}</h1>
      <button onClick={cntp}>+</button>
      <button onClick={cntm}>-</button>
        <hr/>
        <h1> {inputValue} </h1>
        <input
            onChange = { e => inputValueHandler(e.target.value )}
            type = "text"
            value = ""
        />
    </div>
  );
}

export default App;
