import React, {useRef} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup'

const TodoForm = (props) => {
	const title = useRef(),
		text = useRef()

	function setData(e){
		e.preventDefault()
		const newTask = {
			id: Date.now(),
			title: title,
			text: text,
			startDate: `${Date.getMonth()}.${Date.getDay<10?'0'+toString(Date.getDay):Date.getDay}`
		}
		console.log(title.current.value)
		console.log(text.current.value)

		title.current.value = ""
		text.current.value = ""
	}

	return (
		<div>
			<InputGroup className="p-3 pb-1">
				<Form.Control placeholder='title' ref={title}/>
				<Form.Control placeholder='text' ref={text}/>
				<Button variant="outline-success" onClick={e=>setData(e)}>create</Button>
			</InputGroup>
		</div>
	);
};

export default TodoForm;
