import React from 'react'
import Card from 'react-bootstrap/Card'
import {TrashIcon, MilestoneIcon} from '@primer/octicons-react'
import Button from 'react-bootstrap/Button'
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import SetStatusEl from './setStatusEl'

const TodoItem = ({data, cnt, deleteItem}) => {

	const statusColor = {
		'passive': "text-secondary",
		'in progress': "text-primary",
		'done': "text-success",
		'deadlines close': "text-warning",
		'too late': "text-danger",
		'important': "text-warning"
	}, statusBg = {
		'passive': "bg-secondary",
		'in progress': "bg-primary",
		'done': "bg-success",
		'deadlines close': "bg-warning",
		'too late': "bg-danger",
		'important': "bg-warning"
	}


	const setStatus = (status) => {

	}

	let dropDownData = []
    for(const status in statusBg)
		 dropDownData.push(<SetStatusEl status={status} value={statusBg[status]} setStatus={setStatus}/>)

	return (
		<Card className="m-2 shadow-sm" border="secondary" style={{width:"24rem"}}>
			<Card.Header className="pe-2 d-flex justify-content-between align-items-center">
				<div className={statusColor[data.status]+" d-flex align-items-center"}>
					<span className="me-2"> {data.status} </span>
					<DropdownButton autoClose={true}>
						{dropDownData}
					</DropdownButton>
				</div>
				<div className="tools d-flex">
					<Button onClick={()=>deleteItem(data.id)} variant="light" className="p-1 d-flex align-content-center">
						<TrashIcon size={24}/>
					</Button>
				</div>
			</Card.Header>
			<Card.Body>
				<Card.Title className="fs-4 text-primary">
					<span> {cnt} </span>{/**/}
					<span> {data.title} </span>
				</Card.Title>
				<Card.Text>
					{data.text}
				</Card.Text>
			</Card.Body>
			<Card.Footer className={"text-muted d-flex justify-content-between bg-opacity-25 "+statusBg[data.status]}>
				<div> {data.startDate} </div>
				<div> {data.endDate} </div>
			</Card.Footer>
		</Card>
	);
};

export default TodoItem
