import React, {useState} from 'react'

function App() {
  const [cnt, cntHandler]  = useState(0)
  const [inputValue, inputValueHandler]  = useState("type smth")

  function cntp () {
    cntHandler( cnt + 1 )
  }
  function cntm () {
    cntHandler( cnt - 1 )
  }

  return (
    <div className="App">
      <h1>{cnt}</h1>
      <button onClick={cntp}>+</button>
      <button onClick={cntm}>-</button>
        <hr/>
        <input
            onChange = {inputValueHandler}
            type = "text"
            value = {inputValue}
        />
    </div>
  );
}

export default App;
