import React, {useRef} from 'react'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import InputGroup from 'react-bootstrap/InputGroup'
import Moment from 'moment'

const TodoForm = ({addTodo}) => {
	const title = useRef(),
		text = useRef(),
		endDate = useRef()

	function setData(e){
		e.preventDefault()
		if(!title.current.value || !text.current.value || !endDate.current.value)
			return
		const currentDate = Moment().format('YYYY-MM-DD'),
			newTask = {
			id: Date.now(),
			title: title.current.value,
			text: text.current.value,
			startDate: `${currentDate}`,
			endDate: endDate.current.value
		}
		console.log(addTodo)
		addTodo(newTask)
		console.log(newTask)

		title.current.value = ''
		text.current.value = ''
		endDate.current.value = ''
	}

	return (
		<div>
			<InputGroup className="p-3 pb-1">
				<Form.Control placeholder='title' ref={title}/>
				<Form.Control placeholder='text' ref={text}/>
				<Form.Control placeholder='deadline' type='date' ref={endDate}/>
				<Button variant="outline-success" onClick={e=>setData(e)}>create</Button>
			</InputGroup>
		</div>
	);
};

export default TodoForm;
